INSERT INTO `<DB_PREFIX>email_templates` (`id`, `language_id`, `template_code`, `template_name`, `template_subject`, `template_content`, `is_system_template`) VALUES
(1, 'en', 'new_account_created', 'Email for new user', 'Your account has been created', 'Dear <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nCongratulations on creating your new account.\r\n\r\nPlease keep this email for your records, as it contains an important information that you may need, should you ever encounter problems or forget your password.\r\n\r\nYour login: {USER NAME}\r\nYour password: {USER PASSWORD}\r\n\r\nYou may follow the link below to log into your account: \r\n<a href={BASE URL}index.php?user=login>Login Here</a>\r\n\r\nP.S. Remember, we will never sell your personal information or email address.\r\n\r\nEnjoy!\r\n-\r\nSincerely,\r\nCustomer Support', 1),
(2, 'es', 'new_account_created', 'Email para el nuevo usuario', 'Tu cuenta ha sido creada', 'Estimado <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nFelicitaciones por la creación de su nueva cuenta.\r\n\r\nPor favor, mantenga este e-mail para sus registros, ya que contiene una información importante que usted pueda necesitar, si surge algún problema u olvidas tu contraseña.\r\n\r\nUsted entrada: {USER NAME}\r\nUsted contraseña: {USER PASSWORD}\r\n\r\nPor favor siga el siguiente enlace para acceder a su cuenta: \r\n<a href={BASE URL}index.php?user=login>Entrar a tu cuenta</a>\r\n\r\nPD Recuerde, nosotros nunca venderemos su nombre o dirección de correo electrónico.\r\n\r\nDisfrute!\r\n-----------------------------\r\nSinceramente,\r\nSoporte al cliente', 1),
(3, 'de', 'new_account_created', 'E-Mail für neue Benutzer', 'Ihr Konto wurde erstellt', 'Liebe <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nHerzlichen Glückwunsch zu Ihrem neuen Konto erstellen.\r\n\r\nBitte bewahren Sie diese E-Mail für Ihre Aufzeichnungen, da sie eine wichtige Information, die Sie benötigen, sollten Sie Probleme auftreten oder Ihr Passwort vergessen enthält.\r\n\r\nIhr Login: {USER NAME}\r\nIhr Passwort: {USER PASSWORD}\r\n\r\nSie können den folgenden Link in Ihren Account einloggen: \r\n<a href={BASE URL}index.php?user=login>Hier anmelden</a>\r\n\r\nP.S. Denken Sie daran, wir werden nie verkaufen Ihren Namen oder E-Mail-Adresse.\r\n\r\nViel Spaß!\r\n-\r\nMit freundlichen Grüßen,\r\nCustomer Support', 1),
(4, 'en', 'new_account_created_confirm_by_email', 'Email for new user (email confirmation required)', 'Your account has been created (confirmation required)', 'Dear <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nCongratulations on creating your new account.\r\n\r\nPlease keep this email for your records, as it contains an important information that you may need, should you ever encounter problems or forget your password.\r\n\r\nYour login: {USER NAME}\r\nYour password: {USER PASSWORD}\r\n\r\nIn order to become authorized member, you will need to confirm your registration. You may follow the link below to reach the confirmation page:\r\n<a href={BASE URL}index.php?user=confirm_registration&c={REGISTRATION CODE}>Confirm</a>\r\n\r\nP.S. Remember, we will never sell your personal information or email address.\r\n\r\nEnjoy!\r\n-\r\nSincerely,\r\nCustomer Support', 1),
(5, 'es', 'new_account_created_confirm_by_email', 'Correo electrónico para el nuevo usuario (se requiere confirmación)', 'Su cuenta ha sido creada (requiere confirmación por e-mail)', 'Estimado <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nFelicitaciones por la creación de su nueva cuenta.\r\n\r\nPor favor, mantenga este e-mail para sus registros, ya que contiene una información importante que usted pueda necesitar, si surge algún problema u olvidas tu contraseña.\r\n\r\nUsted entrada: {USER NAME}\r\nUsted contraseña: {USER PASSWORD}\r\n\r\nPara convertirse en miembro autorizado, tendrá que confirmar su registro. Por favor siga el siguiente enlace para acceder a la página de confirmación:\r\n<a href={BASE URL}index.php?user=confirm_registration&c={REGISTRATION CODE}>Confirmar</a>\r\n\r\nPD Recuerde, nosotros nunca venderemos su nombre o dirección de correo electrónico.\r\n\r\nDisfrute!\r\n-\r\nSinceramente,\r\nSoporte al cliente', 1),
(6, 'de', 'new_account_created_confirm_by_email', 'E-Mail für neue Benutzer (E-Mail-Bestätigung erforderlich)', 'Ihr Konto wurde erstellt (Bestätigung erforderlich)', 'Liebe <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nHerzlichen Glückwunsch zu Ihrem neuen Konto erstellen.\r\n\r\nBitte bewahren Sie diese E-Mail für Ihre Aufzeichnungen, da sie eine wichtige Information, die Sie benötigen, sollten Sie Probleme auftreten oder Ihr Passwort vergessen enthält.\r\n\r\nIhr Login: {USER NAME}\r\nIhr Passwort: {USER PASSWORD}\r\n\r\nUm Mitglied zu werden ermächtigt, müssen Sie Ihre Registrierung bestätigen. Sie können folgen Sie den untenstehenden Link, um die Bestätigungs-Seite zu erreichen:\r\n<a href={BASE URL}index.php?user=confirm_registration&c={REGISTRATION CODE}>Bestätigen</a>\r\n\r\nP.S. Denken Sie daran, wir werden nie verkaufen Ihren Namen oder E-Mail-Adresse.\r\n\r\nViel Spaß!\r\n-\r\nMit freundlichen Grüßen,\r\nCustomer Support', 1),
(7, 'en', 'account_deleted_by_user', 'Account removed email (by user) ', 'Your account has been removed ', 'Dear {USER NAME}!\r\n\r\nYour account was removed.\r\n\r\n-\r\nSincerely,\r\nCustomer Support', 1),
(8, 'es', 'account_deleted_by_user', 'Cuenta Eliminada correo electrónico (por el usuario)', 'Su cuenta ha sido fue eliminado', 'Estimado {USER NAME}!\r\n\r\nTu cuenta ha sido eliminada.\r\n\r\n-----------------------------\r\nAtentamente,\r\nAtención al cliente', 1),
(9, 'de', 'account_deleted_by_user', 'Konto entfernt E-Mail (durch Benutzer)', 'Ihr Konto wurde entfernt', 'Sehr geehrter {USER NAME}!\r\n\r\nIhr Konto wurde entfernt.\r\n\r\n-\r\nMit freundlichen Grüßen,\r\nCustomer Support', 1),
(10, 'en', 'password_forgotten', 'Email for user or admin forgotten password', 'Forgotten Password', 'Hello <b>{USER NAME}</b>!\r\n\r\nYou or someone else asked for your login info on our site:\r\n{WEB SITE}\r\n\r\nYour Login Info:\r\n------------------------\r\nUsername: {USER NAME}\r\nPassword: {USER PASSWORD}\r\n\r\n------------------\r\nBest regards,\r\n{WEB SITE}\r\n', 1),
(11, 'es', 'password_forgotten', 'Email para el usuario o administrador contraseña olvidada', 'He olvidado la contraseña', 'Hola <b>{USER NAME}</b>!\r\n\r\nUsted o alguien más pide su información de acceso en nuestro sitio:\r\n{WEB SITE}\r\n\r\nSu usuario Info:\r\n------------------------\r\nNombre de usuario: {USER NAME}\r\nContraseña: {USER PASSWORD}\r\n\r\n------------------\r\nSaludos cordiales,\r\n{WEB SITE}', 1),
(12, 'de', 'password_forgotten', 'E-Mail für Benutzer oder Administrator Passwort vergessen', 'Passwort vergessen', 'Hallo <b>{USER NAME}</b>!\r\n\r\nSie oder jemand anderes baten um Ihre Login-Infos auf unserer Website:\r\n{WEB SITE}\r\n\r\nIhre Login-Infos:\r\n\r\nBenutzername: {USER NAME}\r\nPasswort: {USER PASSWORD}\r\n\r\n\r\nMit freundlichen Grüßen,\r\n{WEB SITE}\r\n', 1),
(13, 'en', 'events_new_registration', 'Events - new member has registered (member copy)', 'You have been successfully registered to the event!', 'Dear <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nCongratulations on registering to {EVENT}.\r\n\r\nPlease keep this email for your records, as it contains an important information that you may need.\r\n\r\n---------------\r\nBest Regards,\r\nAdministration', 1),
(14, 'es', 'events_new_registration', 'Eventos - nuevo miembro se ha registrado (copia de miembro)', 'Se le ha registrado con éxito en el evento!', 'Estimado <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nFelicitaciones por el registro de {EVENT}.\r\n\r\nPor favor, mantenga este e-mail para sus registros, ya que contiene una información importante que usted pueda necesitar.\r\n\r\n-------------------\r\nSaludos cordiales,\r\nAdministración', 1),
(15, 'de', 'events_new_registration', 'Veranstaltungen - neues Mitglied registriert hat (Mitglied Kopie)', 'Sie haben erfolgreich an der Veranstaltung angemeldet haben!', 'Lieber <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nHerzlichen Glückwunsch zu der Registrierung zu {EVENT}.\r\n\r\nBitte bewahren Sie diese E-Mail für Ihre Aufzeichnungen, da sie eine wichtige Information, die Sie benötigen enthält.\r\n\r\n-\r\nMit besten Grüßen,\r\nVerwaltung', 1),
(16, 'en', 'test_template', 'Testing Email', 'Testing Email', 'Hello <b>{USER NAME}</b>!\r\n\r\nThis a testing email.\r\n\r\nBest regards,\r\n{WEB SITE}', 0),
(17, 'es', 'test_template', 'Prueba de correo electrónico', 'Prueba de correo electrónico', 'Hola <b>{USER NAME}</b>!\r\n\r\nEsta es una prueba de correo electrónico.\r\n\r\nSaludos cordiales,\r\n{WEB SITE}', 0),
(18, 'de', 'test_template', 'Testen E-Mail', 'Testen E-Mail', 'Hallo <b>{USER NAME}</b>!\r\n\r\nDies ist ein Test E-Mail.\r\n\r\nMit freundlichen Grüßen,\r\n{WEB SITE}', 0),
(19, 'en', 'new_account_created_by_admin', 'New account created (by admin)', 'Your account has been created by administrator', 'Dear <b>{FIRST NAME} {LAST NAME}!</b>\r\n\r\nThe {WEB SITE} Admin has invited you to contribute to our site.\r\n\r\nPlease keep this email for your records, as it contains an important information that you may need, should you ever encounter problems or forget your password.\r\n\r\nYour login: {USER NAME}\r\nYour password: {USER PASSWORD}\r\n\r\nPlease follow the link below to log into your account:\r\n<a href={BASE URL}index.php?user=login>Login</a>\r\n\r\nEnjoy!\r\n-\r\nSincerely,\r\nAdministration', 1),
(20, 'es', 'new_account_created_by_admin', 'Nueva cuenta creada (por admin)', 'Su cuenta ha sido creada por el administrador', 'Estimado <b>{FIRST NAME} {LAST NAME}</b>\r\n\r\nLa {WEB SITE} Admin te ha invitado a contribuir a nuestro sitio.\r\n\r\nPor favor, mantenga este e-mail para sus registros, ya que contiene una información importante que usted pueda necesitar, si surge algún problema u olvidas tu contraseña.\r\n\r\nUsted entrada: {USER NAME}\r\nUsted contraseña: {USER PASSWORD}\r\n\r\nPor favor siga el siguiente enlace para acceder a su cuenta:\r\n<a href={BASE URL}index.php?user=login>Login</a>\r\n\r\nDisfrute!\r\n-\r\nAtentamente,\r\nAdministración', 1),
(21, 'de', 'new_account_created_by_admin', 'Neues Konto erstellt (von admin)', 'Ihr Konto wurde vom Administrator erstellt wurde', 'Lieber <b>{FIRST NAME} {LAST NAME}!</b>\r\n\r\nDie {WEB SITE} Admin hat Sie eingeladen, auf unserer Website beitragen.\r\n\r\nBitte bewahren Sie diese E-Mail für Ihre Aufzeichnungen, da sie eine wichtige Information, die Sie benötigen, sollten Sie Probleme auftreten oder Ihr Passwort vergessen enthält.\r\n\r\nSie Login: {USER NAME}\r\nIhr Passwort: {USER PASSWORD}\r\n\r\nBitte folgen Sie den untenstehenden Link, um in Ihren Account einloggen:\r\n<a href={BASE URL}index.php?user=login>Anmelden</a>\r\n\r\nViel Spaß!\r\n-\r\nMit freundlichen Grüßen,\r\nVerwaltung', 1),
(22, 'en', 'new_account_created_confirm_by_admin', 'Email for new user (admin approval required)', 'Your account has been created (approval required)', 'Dear <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nCongratulations on creating your new account.\r\n\r\nPlease keep this email for your records, as it contains an important information that you may need, should you ever encounter problems or forget your password.\r\n\r\nYour login: {USER NAME}\r\nYour password: {USER PASSWORD}\r\n\r\nAfter your registration will be approved by administrator,  you could log into your account with following link:\r\n<a href={BASE URL}index.php?user=login>Login</a>\r\n\r\nP.S. Remember, we will never sell your name or email address.\r\n\r\nEnjoy!\r\n-\r\nSincerely,\r\nCustomer Support', 1),
(23, 'es', 'new_account_created_confirm_by_admin', 'E-mail para el nuevo usuario (requiere aprobación del administrador)', 'Su cuenta ha sido creada (requiere aprobación)', 'Estimados <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nFelicitaciones por la creación de su nueva cuenta.\r\n\r\nPor favor, mantenga este e-mail para sus registros, ya que contiene una información importante que usted pueda necesitar, si surge algún problema u olvidas tu contraseña.\r\n\r\nSu entrada: {USER NAME}\r\nSu contraseña: {USER PASSWORD}\r\n\r\nDespués de su registro será aprobado por el administrador, puede acceder a su cuenta con el siguiente enlace:\r\n<a href={BASE URL}index.php?user=login>Login</a>\r\n\r\nPD Recuerde, nosotros nunca venderemos su nombre o dirección de correo electrónico.\r\n\r\nDisfrute!\r\n-\r\nAtentamente,\r\nAtención al cliente', 1),
(24, 'de', 'new_account_created_confirm_by_admin', 'E-Mail für neue Benutzer (admin Genehmigung erforderlich)', 'Ihr Konto wurde erstellt (Bestätigung erforderlich)', 'Liebe <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nHerzlichen Glückwunsch zu Ihrem neuen Konto erstellen.\r\n\r\nBitte bewahren Sie diese E-Mail für Ihre Aufzeichnungen, da sie eine wichtige Information, die Sie benötigen, sollten Sie Probleme auftreten oder Ihr Passwort vergessen enthält.\r\n\r\nIhr Login: {USER NAME}\r\nIhr Passwort: {USER PASSWORD}\r\n\r\nNach Ihrer Anmeldung vom Administrator genehmigt werden, könnten Sie Ihr Konto mit folgenden Link anmelden:\r\n<a href={BASE URL}index.php?user=login>Anmelden</a>\r\n\r\nP.S. Denken Sie daran, wir werden nie verkaufen Ihren Namen oder E-Mail-Adresse.\r\n\r\nViel Spaß!\r\n-\r\nMit freundlichen Grüßen,\r\nCustomer Support', 1),
(25, 'de', 'new_account_created_notify_admin', 'Neues Konto erstellt wurde (benachrichtigen admin)', 'Neues Konto erstellt wurde', 'Hallo Admin!\r\n\r\nEin neuer Benutzer hat bei Ihnen vor Ort registriert worden.\r\nDiese Email enthält einen Benutzer-Account Details:\r\n\r\nName: {FIRST NAME} {LAST NAME}\r\nE-Mail: {USER EMAIL}\r\nBenutzername: {USER NAME}\r\n\r\nP.S. Bitte überprüfen Sie, ob es nicht erforderlich, Ihre Zustimmung zur Aktivierung.', 1),
(26, 'es', 'new_account_created_notify_admin', 'Nueva cuenta se ha creado (notificar admin)', 'Nueva cuenta se ha creado', 'Hola admin!\r\n\r\nUn nuevo usuario ha sido registrado en su sitio.\r\nEste correo electrónico contiene una información de la cuenta de usuario:\r\n\r\nNombre: {FIRST NAME} {LAST NAME}\r\nE-mail: {USER EMAIL}\r\nNombre de usuario: {USER NAME}\r\n\r\nPD Por favor, compruebe si no se requiere su aprobación para la activación.', 1),
(27, 'en', 'new_account_created_notify_admin', 'New account has been created (notify admin)', 'New account has been created', 'Hello Admin!\r\n\r\nA new user has been registered at your site.\r\nThis email contains a user account details:\r\n\r\nName: {FIRST NAME} {LAST NAME}\r\nEmail: {USER EMAIL}\r\nUsername: {USER NAME}\r\n\r\nP.S. Please check if it doesn''t require your approval for activation.', 1),
(28, 'en', 'registration_approved_by_admin', 'Email for new user (registration was approved by admin)', 'Your registration has been approved', 'Dear <b>{FIRST NAME} {LAST NAME}!</b>\r\n\r\nCongratulations! This e-mail is to confirm that your registration at {WEB SITE} has been approved.\r\n\r\nYou may now login in to your account.\r\n\r\nThanks for choosing {WEB SITE}.\r\n-\r\nSincerely,\r\nAdministration', 1),
(29, 'es', 'registration_approved_by_admin', 'E-mail para el nuevo usuario (el registro fue aprobado por el administrador)', 'Su registro ha sido aprobado', 'Estimado <b>{FIRST NAME} {LAST NAME}!</b>\r\n\r\n¡Felicitaciones! Este e-mail es para confirmar que su inscripción en {WEB SITE} ha sido aprobado.\r\n\r\nAhora puede acceder a su cuenta ahora.\r\n\r\nGracias por elegir {WEB SITE}.\r\n-\r\nAtentamente,\r\nadministración', 1),
(30, 'de', 'registration_approved_by_admin', 'E-Mail für neue Benutzer (Registrierung wurde von admin zugelassen)', 'Ihre Registrierung genehmigt wurde', 'Sehr geehrte <b>{FIRST NAME} {LAST NAME}!</b>\r\n\r\nHerzlichen Glückwunsch! Diese E-Mail ist zu bestätigen, dass Ihre Anmeldung bei {WEB SITE} genehmigt wurde.\r\n\r\nSie können jetzt in Ihr Konto jetzt einloggen.\r\n\r\nDanke für die Wahl {WEB SITE}.\r\n-\r\nMit freundlichen Grüßen,\r\nVerwaltung', 1),
(31, 'en', 'password_changed_by_admin', 'Password changed by admin', 'Your password has been changed', 'Hello <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nYour password was changed by administrator of the site:\r\n{WEB SITE}\r\n\r\nHere your new login info:\r\n-\r\nUsername: {USER NAME} \r\nPassword: {USER PASSWORD}\r\n\r\n-\r\nBest regards,\r\nAdministration', 1),
(32, 'es', 'password_changed_by_admin', 'Contraseña cambiada por admin', 'Su contraseña ha sido cambiada', 'Hola <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nSu contraseña se ha cambiado por el administrador del sitio:\r\n{WEB SITE}\r\n\r\nAquí la información de su nuevo inicio de sesión:\r\n-\r\nNombre de usuario: {USER NAME} \r\nContraseña: {USER PASSWORD}\r\n\r\n-\r\nSaludos cordiales,\r\nAdministración', 1),
(33, 'de', 'password_changed_by_admin', 'Kennwort von admin geändert', 'Ihr Passwort wurde geändert', 'Hallo <b>{FIRST NAME} {LAST NAME}</b>!\r\n\r\nIhr Passwort wurde durch den Administrator der Website geändert:\r\n{WEB SITE}\r\n\r\nHier werden Ihre neuen Login-Info:\r\n-\r\nBenutzername: {USER NAME} \r\nKennwort: {USER PASSWORD}\r\n\r\n-\r\nMit freundlichen Grüßen,\r\nVerwaltung', 1),
(34, 'en', 'unsubscription_from_newsletter', 'Newsletter - member has unsubscribed (member copy)', 'You have been unsubscribed from the Newsletter', 'Hello!\r\n\r\nYou are receiving this email because you, or someone using this email address, unsubscribed from the Newsletter of {WEB SITE}\r\n\r\nYou can always restore your subscription, using the link below: \r\n<a href="{BASE URL}index.php?page=newsletter&task=pre_subscribe&email={USER EMAIL}">Subscribe</a>\r\n\r\n-\r\nBest Regards,\r\nAdministration', 1),
(35, 'es', 'unsubscription_from_newsletter', 'Newsletter - miembro ha dado de baja (copia miembros)', 'Se le ha cancelado la suscripción a la Newsletter', 'Hola!\r\n\r\nUsted está recibiendo este correo electrónico porque usted o alguien que use la siguiente dirección de correo electrónico, de baja de la Newsletter de {WEB SITE}\r\n\r\nSiempre podrá restaurar su suscripción, usando el siguiente enlace: \r\n<a href="{BASE URL}index.php?page=newsletter&task=pre_subscribe&email={USER EMAIL}">Suscribir</a>\r\n\r\n-\r\nSaludos cordiales,\r\nadministración', 1),
(36, 'de', 'unsubscription_from_newsletter', 'Newsletter - Mitglied hat sich abgemeldet (Mitglied Kopie)', 'Sie haben den Newsletter abbestellt worden', 'Hallo!\r\n\r\nSie erhalten diese E-Mail, weil Sie oder jemand mit dieser E-Mail-Adresse, von der Newsletter von {WEB SITE} abgemeldet\r\n\r\nSie können immer wieder Ihr Abonnement, über den Link unten: \r\n<a href="{BASE URL}index.php?page=newsletter&task=pre_subscribe&email={USER EMAIL}">Zeichnen</a>\r\n-\r\nMit freundlichen Grüßen,\r\nVerwaltung', 1),
(37, 'en', 'subscription_to_newsletter', 'Newsletter - new member has subscribed (member copy)', 'You have been subscribed to the Newsletter', 'Hello!\r\n\r\nYou are receiving this email because you, or someone using this email address, subscribed to the Newsletter of {WEB SITE}\r\n\r\nIf you do not wish to receive such emails in the future, please click this link: <a href="{BASE URL}index.php?page=newsletter&task=pre_unsubscribe&email={USER EMAIL}">Unsubscribe</a>\r\n\r\n-\r\nBest Regards,\r\nAdministration', 1),
(38, 'es', 'subscription_to_newsletter', 'Newsletter - nuevo miembro se ha suscrito (copia miembros)', 'Que se han suscrito al boletín de noticias', 'Hola!\r\n\r\nUsted está recibiendo este correo electrónico porque usted o alguien que use la siguiente dirección de correo electrónico, suscritas al boletín de noticias de {WEB SITE}\r\n\r\nSi usted no desea recibir dichos correos electrónicos en el futuro, por favor haga clic en este enlace: <a href="{BASE URL}index.php?page=newsletter&task=pre_unsubscribe&email={USER EMAIL}">Cancelar la suscripción</a>\r\n\r\n-\r\nSaludos cordiales,\r\nadministración', 1),
(39, 'de', 'subscription_to_newsletter', 'Newsletter - neues Mitglied abonniert hat (Mitglied Kopie)', 'Sie haben den Newsletter abonniert haben', 'Hallo!\r\n\r\nSie erhalten diese E-Mail, weil Sie oder jemand mit dieser E-Mail-Adresse, den Newsletter von {WEB SITE} abonniert\r\n\r\nWenn Sie nicht möchten, dass solche E-Mails in der Zukunft erhalten, klicken Sie bitte diesen Link: <a href="{BASE URL}index.php?page=newsletter&task=pre_unsubscribe&email={USER EMAIL}">Abmelden</a>\r\n\r\n-\r\nMit freundlichen Grüßen,\r\nVerwaltung', 1);
